import { Avatar } from '@material-ui/core';
import React from 'react';
import { GrClose } from 'react-icons/gr';
import classes from './Cart.module.css';
import Drawer from '@material-ui/core/Drawer';

const Cart = (props) => {
  return (
    <Drawer
      anchor={'right'}
      open={true}
      onClose={() => props.setOpenCart(false)}
    >
      <div className={classes.cart}>
        <div className={classes.header_cart}>
          <div className={classes.close}>
            <GrClose />
          </div>
          <div className={classes.card}>GIỎ HÀNG</div>
        </div>
        <div className={classes.cart_info}>
          <div className={classes.product}>
            <div className={classes.avatar}>
              <Avatar
                alt="Remy Sharp"
                variant="square"
                style={{
                  width: '100%',
                  height: '100%',
                }}
                src="https://oversizedshop.com.vn/ovs/uploads/2022/02/DSC00389-570x760.jpg"
              />
            </div>
            <div className={classes.info}>
              <div className={classes.name}>
                Áo peplum Oversized thêu hoa nhí [ZA6]
              </div>
              <div className={classes.price}>1 x 290.000₫</div>
            </div>
          </div>
          <div className={classes.closeProduct}>
            <GrClose />
          </div>
        </div>

        <div className={classes.amount}>
          <div>Tổng số phụ:</div>
          <div>290.000₫</div>
        </div>
        <div className={classes.actions}>
          <button>XEM GIỎ HÀNG</button>
          <button>THANH TOÁN </button>
          <button>CONTINUE SHOPPING</button>
        </div>
      </div>
    </Drawer>
  );
};

export default Cart;
